import React from 'react';
import { useQuery } from '@apollo/client';
import { GET_BRAND_NAV } from '../queries';
import { AEMHeadless } from '@adobe/aem-headless-client-js';

const Navigation = () => {
  const aemHeadlessClient = new AEMHeadless({
    serviceURL: 'http://localhost:4502',
    endpoint: '/content/cq:graphql/perficientpocs/endpoint',
    auth: ['admin', 'admin'],
  })

  const { loading, error, data } = aemHeadlessClient.runQuery(GET_BRAND_NAV);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: Could not fetch navigation items.</p>;

  return (
    <nav>
      <ul>
        {data.navigation.items.map((item) => (
          <li key={item.id}>
            <a href={item.url}>{item.title}</a>
          </li>
        ))}
      </ul>
    </nav>
  );
};

export default Navigation;
