// src/components/Navigation.js
import React, { useEffect, useState } from 'react';
import { getTheData } from '../utils/client';

const Navigation = () => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const username = 'admin';
      const password = 'admin';
      const encodedCredentials = btoa(`${username}:${password}`);
      const endpoint = 'http://localhost:4502/graphql/execute.json/perficientpocs/get-logo-and-nav-links';
      const headers = {
        'Content-Type': 'application/json',
        'Authorization': `Basic ${encodedCredentials}`
      };

      try {
        const result = await getTheData(endpoint, headers, true);
        setData(result);
        console.log(result);
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  return (
    <nav>

        <section class="logo-search-mobile-container">
            <div class="brand">
                <a href="/" aria-label="Go to the home page" class="logo link">
                   <img src="http://localhost:4502"{...data.data.navigationCfModelList.items[0].brandLogo[0]} width=
                   '140' height='45' alt="Logo"></img>
                </a>
            </div>

            <div class="nav-icons">
                <button aria-label="Search navigation" aria-expanded="false" class="search">
                    <svg class="search-icon" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 22 22">
                    <path d="m132.77 118.03l-27.945-27.945c6.735-9.722 10.1-20.559 10.1-32.508 0-7.767-1.508-15.195-4.523-22.283-3.01-7.089-7.088-13.199-12.221-18.332-5.133-5.133-11.242-9.207-18.33-12.221-7.09-3.01-14.518-4.522-22.285-4.522-7.767 0-15.195 1.507-22.283 4.522-7.089 3.01-13.199 7.088-18.332 12.221-5.133 5.133-9.207 11.244-12.221 18.332-3.01 7.089-4.522 14.516-4.522 22.283 0 7.767 1.507 15.193 4.522 22.283 3.01 7.088 7.088 13.197 12.221 18.33 5.133 5.134 11.244 9.207 18.332 12.222 7.089 3.02 14.516 4.522 22.283 4.522 11.951 0 22.787-3.369 32.509-10.1l27.945 27.863c1.955 2.064 4.397 3.096 7.332 3.096 2.824 0 5.27-1.032 7.332-3.096 2.064-2.063 3.096-4.508 3.096-7.332.0001-2.877-1-5.322-3.01-7.331m-49.41-34.668c-7.143 7.143-15.738 10.714-25.787 10.714-10.05 0-18.643-3.572-25.786-10.714-7.143-7.143-10.714-15.737-10.714-25.786 0-10.05 3.572-18.644 10.714-25.786 7.142-7.143 15.738-10.714 25.786-10.714 10.05 0 18.643 3.572 25.787 10.714 7.143 7.142 10.715 15.738 10.715 25.786 0 10.05-3.573 18.643-10.715 25.786" transform="matrix(.11417.00745-.00745.11417 3.93 2.548)"/></svg>
                </button>

                <button id="mobile-nav" aria-label="Mobile navigation" aria-expanded="false" class="mobile-nav">
                    <svg class="mobile-nav-icon" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 448 512"><path d="M0 84V44c0-8.837 7.163-16 16-16h416c8.837 0 16 7.163 16 16v40c0 8.837-7.163 16-16 16H16c-8.837 0-16-7.163-16-16zm16 144h416c8.837 0 16-7.163 16-16v-40c0-8.837-7.163-16-16-16H16c-8.837 0-16 7.163-16 16v40c0 8.837 7.163 16 16 16zm0 256h416c8.837 0 16-7.163 16-16v-40c0-8.837-7.163-16-16-16H16c-8.837 0-16 7.163-16 16v40c0 8.837 7.163 16 16 16zm0-128h416c8.837 0 16-7.163 16-16v-40c0-8.837-7.163-16-16-16H16c-8.837 0-16 7.163-16 16v40c0 8.837 7.163 16 16 16z"/></svg>
                </button>
            </div>
        </section>

      <ul>
        <li class="site-menu-item">
                    <a href="${properties.homePageLink}.html" class="site-menu-link link">Your Profile</a>
        </li>
        {data.data.navigationCfModelList.items[0].generatedNavigationList.map((item) => (
          <li key={item.id}>
            <a href={item.path}>{item.title}</a>
          </li>
        ))}
      </ul>
    </nav>
  );
};

export default Navigation;
