// src/components/Navigation.js
import React, { useEffect, useState } from 'react';
import { getTheData } from '../utils/client';

const Navigation = () => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const headers = {
        'Authorization': 'Bearer your-auth-token', // Replace with your token
        'X-Custom-Header': 'your-custom-header-value', // Replace with your custom header
      };

      try {
        const result = await getTheData(headers);
        setData(result);
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  return (
    <nav>
      <ul>
        {data.navigation.items.map((item) => (
          <li key={item.id}>
            <a href={item.url}>{item.title}</a>
          </li>
        ))}
      </ul>
    </nav>
  );
};

export default Navigation;
